import React, { useState, useEffect } from 'react';
import axios from 'axios';
import './AssignmentPage.css'; // Import the CSS file

const AssignmentPage = () => {
  const [assignmentNumber, setAssignmentNumber] = useState('');
  const [assignmentType, setAssignmentType] = useState('homework');
  const [githubUrl, setGithubUrl] = useState('');
  const [branchName, setBranchName] = useState('');
  const [error, setError] = useState('');
  const [success, setSuccess] = useState('');

  useEffect(() => {
    const fetchAssignmentNumber = async () => {
      try {
        const response = await axios.get('/api/assignments/nextAssignmentNumber');
        setAssignmentNumber(response.data.assignmentNumber);
      } catch (err) {
        setError('Failed to fetch assignment number');
      }
    };

    fetchAssignmentNumber();
  }, []);

  const handleSubmit = async (e) => {
    e.preventDefault();
    try {
      const newAssignment = {
        assignmentType,
        githubUrl,
        branchName,
      };
      const response = await axios.post('/api/assignments', newAssignment);
      setSuccess('Assignment submitted successfully!');
      setError('');
    } catch (err) {
      setError('Failed to submit assignment');
      setSuccess('');
    }
  };

  return (
    <div className="assignment-container">
      <form onSubmit={handleSubmit} className="assignment-form">
        <h1 className="assignment-title">Assignment #{assignmentNumber}</h1>
        {error && <p className="assignment-error">{error}</p>}
        {success && <p className="assignment-success">{success}</p>}
        <label htmlFor="assignmentType">Assignment Type:</label>
        <select
          id="assignmentType"
          value={assignmentType}
          onChange={(e) => setAssignmentType(e.target.value)}
          className="assignment-input"
        >
          <option value="homework">Homework</option>
          <option value="project">Project</option>
          <option value="quiz">Quiz</option>
        </select>
        <input
          type="url"
          placeholder="GitHub URL"
          value={githubUrl}
          onChange={(e) => setGithubUrl(e.target.value)}
          className="assignment-input"
          required
        />
        <input
          type="text"
          placeholder="Branch Name"
          value={branchName}
          onChange={(e) => setBranchName(e.target.value)}
          className="assignment-input"
          required
        />
        <button type="submit" className="assignment-button">Submit</button>
        <button
          type="button"
          className="assignment-button"
          onClick={() => window.location.href = 'dashboard.html'}
        >
          Back to Dashboard
        </button>
      </form>
    </div>
  );
};

export default AssignmentPage;
